/**
 * @ngdoc directive
 * @name xui.directive:xuiContent
 * @restrict E
 *
 * @description
 * Content wrapper. The purpose of this component is to hold content inside of it.
 *
 * @parameters
 * @param {string} [size='normal'] Size of container. Values: 'large', 'normal', or 'small'.

 *
 *
 * @example
 *    <example module="xui">
 *        <file src="src/components/content/docs/content-examples.html" name="index.html"></file>
 *        <file src="src/components/content/docs/content-examples.js" name="script.js"></file>
 *    </example>
 */
const sizeMap:{[id:string]:string} = {
    "large": "xui-content-lg",
    "small": "xui-content-sm"
};

export default class Content implements ng.IDirective {
    public static $inject = ["swUtil"];

    constructor(private swUtil:any) {
    }

    public restrict = "E";
    public templateUrl = "xui/components/content/content-directive.html";
    public replace = true;
    public transclude = true;
    public scope = {
        name: "@",
        size: "@"
    };

    public link:ng.IDirectiveLinkFn = (scope:ng.IScope, element:ng.IAugmentedJQuery,
                                            attrs:ng.IAttributes) => {
        //name
        this.swUtil.initComponent(attrs, "content");

        //size
        if (attrs["size"]) {
            element.addClass(sizeMap[attrs["size"].toLowerCase()]);
        }
    };
}
