class DatepickerPopup implements ng.IDirective {

    public restrict = "EAC";
    public require = "ngModel";
    public link:ng.IDirectiveLinkFn = (scope:ng.IScope, element:ng.IAugmentedJQuery,
                                            attrs:ng.IAttributes, ctrl:any) => {
        // This fixes defect in bootstrap datepicker popup that display initial date as unformatted
        // string https://github.com/angular-ui/bootstrap/issues/2659
        ctrl.$formatters.shift();
    };

}

export default DatepickerPopup;
