(function () {
    "use strict";

    Xui.module
        .controller("DemoDatepickerController", DemoDatepickerController);

    function DemoDatepickerController() {
        var vm = this;

        vm.today = new Date();
        vm.dt = vm.today;
        vm.dt2 = new Date("October 13, 2014 11:13:00");
        vm.dt3 = new Date();
        vm.disablePopup = false;
        vm.disableInline = false;

        vm.onBlur = function() {
            console.log("Blur at", new Date());
        };

        vm.onFocus = function() {
            console.log("Focus at", new Date());
        };
    }
})();
