import Module from "../../common/module";
import {DatePicker} from "./datePicker-directive";
import DatePickerController from "./datePicker-controller";
import xuiStatic from "../../xui-static";

export default (module:Module)=> {
    // this is uib directive patch
    //module.component("uibDatepickerPopup", DatepickerPopup);

    module.component("xuiDatePicker", DatePicker);
    module.controller("xuiDatePickerController", DatePickerController);

    const translations = require("./datePicker-translations.json") as {[key:string]:string};
    module.app("components").config(xuiStatic.registerTranslations(translations));
};
