import Module from "../../common/module";

import Dialog from "./dialog-directive";
import DialogInstanceController from "./dialogInstance-controller";
import {DialogService} from "./dialog-service";
import {DialogServiceObsolete} from "./dialog-service";
import xuiStatic from "../../xui-static";

export default (module:Module)=> {
    module.component("xuiDialog", Dialog);
    module.controller("xuiDialogInstanceController", DialogInstanceController);
    module.service("xuiDialogService", DialogService);
    module.service("dialogService", DialogServiceObsolete);

    const translations = require("./dialog-translations.json") as {[key:string]:string};
    module.app("components").config(xuiStatic.registerTranslations(translations));
};
