
describe('components >', function () {
	describe('divider >', function () {
        var element, scope, compile;

        beforeEach(angular.mock.module('xui'));

        beforeEach(inject(function ($templateCache, $rootScope, _$compile_) {
            scope = $rootScope.$new();
            compile = _$compile_;
        }));

        var plainDirective = '<xui-divider></xui-divider>';

        it('component should compile', function () {
            var compiledElement = compile(plainDirective)(scope);
            scope.$digest();

            var directiveScope = compiledElement.scope();

            expect(compiledElement[0].outerHTML).toEqual('<hr class="xui-divider">');
        });
    });
});
