import xuiDragEvent = xui.xuiDragEvent;
export default class DraggableController implements xui.IDraggableController {
    constructor() {
        this.onDragStart = this.onDragStart || angular.noop;
        this.onDragEnd = this.onDragEnd || angular.noop;
    }

    public onDragStart:(params:{event: xuiDragEvent}) => void;
    public onDragEnd:(params:{event: xuiDragEvent}) => void;
    public adornerDragClass: string;

    public dragStart = (event:xuiDragEvent) => {
        this.onDragStart({ event: event });
    };

    public dragEnd = (event:xuiDragEvent) => {
        this.onDragEnd({ event: event });
    };
}
