(function () {
    "use strict";

    Xui.module
        .controller("DemoEmptyController", DemoEmptyController, ["$templateCache"]);

    function DemoEmptyController($templateCache) {
        var vm = this;

        vm.customTemplateViewModel = {
            image: "http://oriondemo.solarwinds.com/ui/modules/platform/images/404-Robot.svg",
            searchTerm: "Node134"
        };

        vm.dataNotAvailable = {
            preset: "dataNotAvailable"
        };
        vm.serverUnreachable = {
            preset: "serverUnreachable"
        };
        vm.dataNotApplicable = {
            preset: "dataNotApplicable"
        };
        vm.emptyDataset = {
            preset: "emptyDataset"
        };
        vm.customDefaultMode = {
            image: "no-search-results",
            title: "Nothing found",
            description: "Try searching for something else"
        };
        vm.customCompactMode = {
            compactMode: true,
            image: "no-alerts",
            title: "Nothing found",
            description: "There are no active alerts. Great job."
        };
        vm.customTemplate = {
            templateUrl: "empty-template",
            viewModel: vm.customTemplateViewModel
        };

        $templateCache.put("empty-template",
            '<div class="empty-container">' +
            '<img ng-src="{{ctrl.viewModel.image}}" alt="No items in the list" class="empty-pic"/>' +
            '<p>No result found for "{{ctrl.viewModel.searchTerm}}"</p>' +
            '</div>'
        );
    }
})();
