(function () {
    "use strict";

    Xui.module
        .controller("DemoExpanderController", DemoExpanderController, ["xuiToastService"]);

    function DemoExpanderController(toaster) {
        var vm = this;
        vm.dynamicValue = "00000000";
        vm.expanded = true;

        vm.onStatusChangedOnExpanderWithIcon = function (isOpen) {
            var state = isOpen ? "opened" : "closed";
            toaster.info("Expander with icon has been " + state);
        };

        vm.onStatusChangedOnSimpleExpander = function (isOpen) {
            var state = isOpen ? "opened" : "closed";
            toaster.info("Expander has been " + state);
        };

        vm.regenerateValue = function() {
            vm.dynamicValue =  Math.round(Math.random() * 1000000);
        };
    }
})();

