(function () {
    "use strict";

    Xui.module
        .controller("DemoFileUploadController", DemoFileUploadController, ["xuiToastService"]);

    function DemoFileUploadController(toastService) {
        var vm = this;

        vm.file = null;

        vm.onChange = function() {
            toastService.success("File name: " + (vm.file ? vm.file.name : "none"), "File changed");
        };
    }
})();
