import Module from "../../common/module";

import FileUploadController from "./fileUpload-controller";
import FileUpload from "./fileUpload-directive";
import xuiStatic from "../../xui-static";

export default (module:Module)=> {
    module.component("xuiFileUpload", FileUpload);
    module.controller("xuiFileUploadController", FileUploadController);

    const translations = require("./fileUpload-translations.json") as {[key:string]:string};
    module.app("components").config(xuiStatic.registerTranslations(translations));
};
