export default class HelpHintDirectiveTransclude implements ng.IDirective {
    public compile = (tElement: ng.IAugmentedJQuery, tAttrs: ng.IAttributes,
                      transcludeFn: ng.ITranscludeFunction) => {
        return {
            post: function (scope: ng.IScope, iElement: ng.IAugmentedJQuery, iAttrs: ng.IAttributes) {
                if (transcludeFn) {
                    transcludeFn(scope, function (clone: any) {
                        iElement.append(clone);
                    });
                }
            }
        };
    };
}
