import Module from "../common/module";

export default (module:Module)=> {
    // index.ts file in component directories must export a default function with (module:Module)=>void signature
    const components:any = require.context(".", true, /\.\/[^\/]*\/index\.ts$/);
    components.keys().forEach((r:any)=> {
        components(r).default(module);
    });
};
