export const listItemPadding:{[key:string]:string} = {
    regular: "regular",
    narrow: "narrow",
    compact: "compact",
    none: "none"
};

export default class ListitemController {
    public isSelected:boolean;
    public paddingSize:string;

    public getPaddingClass() {
        const paddingClass = listItemPadding[this.paddingSize] || listItemPadding["regular"];
        return `xui-listitem--${paddingClass}`;
    }
}
