import Module from "../../common/module";

import ListViewController from "./listview-controller";
import ListView from "./listview-directive";

export default (module:Module)=> {
    module.component("xuiListview", ListView);
    module.controller("xuiListviewController", ListViewController);

    /* @ngInject */
    function templates($templateCache:ng.ITemplateCacheService) {
        $templateCache.put("xui-listview-default", require("./templates/listview-default.html"));
        $templateCache.put("xui-listview-multi", require("./templates/listview-multi.html"));
        $templateCache.put("xui-listview-missing", require("./templates/listview-missing.html"));
    }
    module.app().run(templates);
};
