import SpecHelper from "../../specHelper";
describe("components >", () => {
    describe("menu >", () => {
        describe("item link >", () => {
            let element:ng.IAugmentedJQuery,
                scope:any,
                compile:ng.ICompileService;

            beforeEach(angular.mock.module("xui"));

            beforeEach(inject(function ($compile:ng.ICompileService, $rootScope:any) {
                compile = $compile;
                scope = $rootScope.$new();
            }));

            const xuiMenuDirectiveName = "xui-menu";
            const xuiMenuDirectiveCtrlMock = {};

            const directive = "<xui-menu-link url='{{url}}' is-disabled='isDisabled'>" +
                "<b>content</b></xui-menu-link>";

            describe("dom manipulations >", () => {
                it("should have 'disabled' class when 'is-disabled' attribute is set to 'true'", () => {
                    // arrange
                    scope.isDisabled = true;

                    // act
                    element = SpecHelper.getCompiledElementRequiringParentCtrl(compile, scope, directive,
                        xuiMenuDirectiveName, xuiMenuDirectiveCtrlMock);

                    // assert
                    expect(element.hasClass("disabled")).toBeTruthy();
                });

                it("should not have 'disabled' class when 'is-disabled' attribute is set to 'false", () => {
                    // arrange
                    scope.isDisabled = false;

                    // act
                    element = SpecHelper.getCompiledElementRequiringParentCtrl(compile, scope, directive,
                        xuiMenuDirectiveName, xuiMenuDirectiveCtrlMock);

                    // assert
                    expect(element.hasClass("disabled")).toBeFalsy();
                });

                it("should use 'url' attribute value as link href value", () => {
                    // arrange
                    scope.url = "http://www.test.com";

                    // act
                    element = SpecHelper.getCompiledElementRequiringParentCtrl(compile, scope, directive,
                        xuiMenuDirectiveName, xuiMenuDirectiveCtrlMock);

                    // assert
                    const el:any = element.find(".xui-menu-item-link")[0];
                    expect(el.href).toContain("http://www.test.com");
                });
            });

            describe("replace >", () => {
                it("should have replaced directive element", () => {
                    // act
                    element = SpecHelper.getCompiledElementRequiringParentCtrl(compile, scope, directive,
                        xuiMenuDirectiveName, xuiMenuDirectiveCtrlMock);

                    // assert
                    expect(element[0].tagName.toLowerCase()).not.toEqual("xui-menu-link");
                });
            });

            describe("transclude >", () => {
                it("should have an ng-transclude directive in it", () => {
                    // act
                    element = SpecHelper.getCompiledElementRequiringParentCtrl(compile, scope, directive,
                        xuiMenuDirectiveName, xuiMenuDirectiveCtrlMock);

                    // assert
                    const transcludeElem = element.find("a[ng-transclude]");
                    expect(transcludeElem.length).toEqual(1);
                });

                it("should have transclude content", () => {
                    // act
                    element = SpecHelper.getCompiledElementRequiringParentCtrl(compile, scope, directive,
                        xuiMenuDirectiveName, xuiMenuDirectiveCtrlMock);

                    // assert
                    const transcludeContentElem = element.find(".xui-menu-item-link")[0];
                    expect(transcludeContentElem.innerHTML).toMatch("<b .*>content</b>");
                });
            });

            describe("isolated scope >", () => {
                it("'isDisabled' on isolated scope should be two-way bound", () => {
                    // arrange
                    scope.isDisabled = true;

                    // act
                    element = SpecHelper.getCompiledElementRequiringParentCtrl(compile, scope, directive,
                        xuiMenuDirectiveName, xuiMenuDirectiveCtrlMock);
                    const isolatedScope:any = element.isolateScope();
                    isolatedScope.isDisabled = false;
                    scope.$apply();

                    // assert
                    expect(scope.isDisabled).toEqual(false);
                });

                it("'url' on isolated scope should be one-way bound", () => {
                    // arrange
                    scope.url = "http://www.test.com";

                    // act
                    element = SpecHelper.getCompiledElementRequiringParentCtrl(compile, scope, directive,
                        xuiMenuDirectiveName, xuiMenuDirectiveCtrlMock);
                    const isolatedScope:any = element.isolateScope();
                    isolatedScope.url = "http://www.othertest.com";
                    scope.$apply();

                    // assert
                    expect(scope.url).toEqual("http://www.test.com");
                });
            });
        });
    });
});
