(function () {
    "use strict";

    Xui.module
        .controller("DemoPageContentController", DemoPageContentController, ["$timeout"]);

    function DemoPageContentController($timeout) {
        var vm = this;

        vm.title = "Default Header";
        vm.showBusy = true;
        vm.hideBusy = function() {
            vm.showBusy = false;
        };
        vm.init = function() {
            $timeout(vm.hideBusy, 10500);
        };

        vm.init();
    }
})();
