import Module from "../../common/module";
import Pager from "./pager-directive";
import {PagerController} from "./pager-controller";
import xuiStatic from "../../xui-static";

export default (module:Module)=> {
    module.component("xuiPager", Pager);
    module.controller("xuiPagerController", PagerController);

    const translations = require("./pager-translations.json") as {[key:string]:string};
    module.app("components").config(xuiStatic.registerTranslations(translations));
};
