(function () {
    "use strict";

    Xui.module
        .controller("DemoPanelController", DemoPanelController, ["xuiToastService"]);

    function DemoPanelController(toastService) {
        var vm = this;

        vm.heading = "FILTER THE RESULTS";
        vm.parentHeight = 200;

        vm.change = function (isCollapsed) {
            if (isCollapsed) {
                toastService.info("Collapsed!")
            } else {
                toastService.info("Expanded!")
            }
        };
    }
})();