export default class PanelController {
    public isCollapsed:boolean;
    public isCollapsible:boolean;
    public collapseChanged: (params:{isCollapsed:boolean}) => void;

    public toggleCollapsed = () => {
        this.isCollapsed = !this.isCollapsed;
    };

    public fireCollapseChanged = () => {
        if (angular.isFunction(this.collapseChanged)) {
            this.collapseChanged({isCollapsed:this.isCollapsed});
        }
    };
};
