import Module from "../../common/module";

import Popover from "./popover-directive";
import { PopoverController } from "./popover-controller";
import PopoverProvider from "./popover-provider";

export default (module: Module) => {
    module.component("xuiPopover", Popover);
    module.controller("xuiPopoverController", PopoverController);
    module.provider("$xuiPopover", PopoverProvider);
};
