export default class PopoverProvider implements ng.IServiceProvider {
    // The default options for popover.
    private defaultOptions = {};

    // The options specified to the provider globally.
    private globalOptions = {};

    public options = (values: xui.IPopoverOptions): xui.IPopoverOptions => {
        this.globalOptions = values;
        return angular.extend(
            {},
            this.defaultOptions,
            this.globalOptions
        );
    };

    public $get = (): xui.IPopoverProvider => {
        const options: xui.IPopoverOptions = angular.extend(
            {},
            this.defaultOptions,
            this.globalOptions
        );

        return {
            options: options
        };
    };
};
