(function () {
    "use strict";

    Xui.module
        .controller('DemoRadioController', DemoRadioController, ["xuiToastService"]);

    function DemoRadioController(toastService) {
        var vm = this;
        vm.radioDisabled = true;
        vm.vegetable = 'onion';

        vm.onRadioButtonClick = function () {
            toastService.info("Radio button clicked!");
        };
    }
})();