var SpecHelper = require("../../specHelper").default;

describe('components >', function () {
	describe('radio-messages >', function () {
        var element, scope, compile;

        beforeEach(angular.mock.module('xui'));

        beforeEach(inject(function($compile, $rootScope) {
            compile = $compile;
            scope = $rootScope.$new();
        }));

        var directive =
            '<form>' +
                '<xui-radio ng-model="model" value="radioValue" is-required="true">radioTitle</xui-radio>' +
                '<xui-radio-messages name="model">' +
                    '<div ng-message="required">radioMessage</div>' +
                '</xui-radio-messages>' +
            '</form>';

        it('should use string content as message', function() {
            element = SpecHelper.getCompiledElement(compile, scope, directive);

            var el = element.find('.xui-has-error')[0];

            expect(el.innerText.trim()).toEqual('radioMessage');
        });
    });
});
