import { ResizableController } from "./resizable-controller";

/**
 * @ngdoc directive
 * @name xui.directive:xui-resizable
 *
 * @description
 * Makes container element editable, adds resizer element according to type
 *
 * @example
 * <example module="xui">
 *     <file src="src/components/resizable/docs/resizable-examples.html" name="index.html"></file>
 *     <file src="src/components/resizable/docs/resizable-examples.js" name="app.js"></file>
 * </example>
 */
export class ResizableDirective {

    public restrict = "A";

    public controller = ResizableController;

    public bindToController = {
        minWidth: "@",
        maxWidth: "@"
    };

}
