/**
 * @ngdoc directive
 *
 * @name xui.directive:xuiScrollbar
 * @restrict A
 *
 * @description
 * Directive sets { overflow: auto } CSS attribute to the element.
 *
 * @example
 * <example module="xui">
 *        <file src="src/components/scrollbar/docs/scrollbar-examples.html" name="index.html"></file>
 *        <file src="src/components/scrollbar/docs/scrollbar-examples.js" name="app.js"></file>
 *        <file src="src/components/scrollbar/docs/scrollbar-examples.css" name="app.css"></file>
 * </example>
 */

export default class Scrollbar implements ng.IDirective {
    public restrict = "A";
    public scope = false;

    public link = (scope: ng.IScope,
        element: ng.IAugmentedJQuery,
        attrs: ng.IAttributes): void => {
        element.css({ overflow: "auto" });
    };
}
