import Module from "../../common/module";

import Search from "./search-directive";
import SearchController from "./search-controller";
import xuiStatic from "../../xui-static";

export default (module:Module)=> {
    module.component("xuiSearch", Search);
    module.controller("xuiSearchController", SearchController);

    const translations = require("./search-translations.json") as {[key:string]:string};
    module.app("components").config(xuiStatic.registerTranslations(translations));
};
