(function() {
    "use strict";

    Xui.module
        .controller("DemoSelectorController", DemoSelectorController, ["xuiToastService"])
        .controller("DemoCustomizedSelectorController", DemoCustomizedSelectorController, ["xuiToastService"]);

    function DemoSelectorController(toastService) {
        var vm = this;

        vm.checked = "false";
        vm.indeterminate = "true";

        vm.onItemsClick = function(item) {
            console.log(item.value);
        };

        vm.onCheckboxClick = function() {
            toastService.success("Checkbox clicked");
        };
    }

    function DemoCustomizedSelectorController(toastService) {
        var vm = this;

        vm.itemsSource = [
            {
                name: "Name 1",
                value: "Value 1"
            },
            {
                name: "Name 2",
                value: "Value 2"
            },
            {
                name: "Name 3",
                value: "Value 3"
            },            {
                name: "Name 4",
                value: "Value 4"
            }
        ];

        vm.checked = "false";
        vm.indeterminate = "true";

        vm.onItemsClick = function (item) {
            toastService.success("Custom item clicked: " + item.value);
        };

        vm.onCheckboxClick = function() {
            toastService.success("Checkbox clicked");
        };
    }
})();
