import Module from "../../common/module";
import {SelectorController} from "./selector-controller";
import Selector from "./selector-directive";
import SelectorService from "./selector-service";
import xuiStatic from "../../xui-static";

export default (module:Module)=> {
    module.controller("xuiSelectorController", SelectorController);
    module.component("xuiSelector", Selector);
    module.service("xuiSelectorService", SelectorService);

    const translations = require("./selector-translations.json") as {[key:string]:string};
    module.app("components").config(xuiStatic.registerTranslations(translations));
};
