import Inject from "../../decorators/di";
import ISelectorItem = xui.ISelectorItem;

class SelectorService implements xui.ISelectorService {
    public getDefaultItems = (allowSelectAllPages:boolean):ISelectorItem[] => {
        return [
            {name: "all", value: this.$translate.instant("xui_selector_all")},
            {name: "none", value: this.$translate.instant("xui_selector_none")}
        ].concat(allowSelectAllPages
                ? [{name: "all pages", value: this.$translate.instant("xui_selector_all_pages")}]
                : [] );
    };

    constructor(@Inject("$translate") private $translate:angular.translate.ITranslateService) { }
}
export default SelectorService;
