(function () {
    "use strict";

    Xui.module
        .controller('DemoFocusController', DemoFocusController, ["$scope", "$timeout"]);

    function DemoFocusController($scope, $timeout) {
        var vm = this;

        vm.vegetable = 'onion';
        vm.carrotFocused = false;
        vm.onionFocused = false;

        vm.setCarrotFocus = function () {
            vm.carrotFocused = true;
            $timeout(function () {
                $scope.$apply(vm.carrotFocused = false);
            }, 4000);
        };

        vm.setOnionFocus = function () {
            vm.onionFocused = true;
            $timeout(function () {
                $scope.$apply(vm.onionFocused = false);
            }, 4000);
        };
    }
})();