(function () {
    "use strict";

    Xui.module.controller('DemoSidebarController', DemoSidebarController);

    DemoSidebarController.$inject = ["$scope"];

    function DemoSidebarController($scope) {
        var vm = this;

        vm.settings = {
            display: false,
            position: 'right',
            shrink: false
        };

        vm.settings2 = {
            display: false,
            position: 'right',
            shrink: true
        };

        vm.settings3 = {
            display: true,
            position: 'left',
            shrink: false,
            expandable: true
        };

        vm.settings4 = {
            display: true,
            position: 'right',
            shrink: false,
            expandable: true
        };

        vm.toggleSidebar = function () {
            vm.settings.display = !vm.settings.display;
        };

        vm.toggleSidebar2 = function () {
            vm.settings2.display = !vm.settings2.display;
        };

        vm.dynamicContent = 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Cras at viverra orci. ' +
            'Sed elementum lacus eget finibus varius. Fusce rhoncus libero augue. ' +
            'Cras cursus purus convallis ex laoreet, a pulvinar neque mollis. ' +
            'Maecenas fermentum, turpis non aliquam suscipit, sapien ligula feugiat lorem, a suscipit nulla ' +
            'Sed mollis iaculis varius. Suspendisse auctor finibus diam, eget laoreet tellus fermentum nec. ' +
            'Fusce iaculis varius lorem at lacinia. Curabitur et cursus eros, at congue lorem. ' +
            'Aenean urna ipsum, sollicitudin sed ultrices nec, malesuada eu lacus. ' +
            'Sed orci mauris, scelerisque vitae consectetur sit amet, venenatis a mauris. ' +
            'Donec consectetur libero eget bibendum consequat. Donec tincidunt molestie viverra.';
    }
})();
