(function () {
    "use strict";

    Xui.module
        .controller("DemoVerticalSlidableController", DemoVerticalSlidableController, ["xuiToastService"])
        .controller("DemoHorizontalSlidableController", DemoHorizontalSlidableController, ["xuiToastService"])
        .controller("DemoBigSlidableController", DemoBigSlidableController, ["xuiToastService"]);

    function DemoVerticalSlidableController(toaster) {
        var vm = this;

        vm.left = 0;
        vm.top = 0;
        vm.containerHeight = 200;
        vm.step = 10;

        vm.onDrag = function (event, position) {
            vm.left = position.left;
            vm.top = position.top;
            return {
                left: vm.left,
                top: vm.top
            };
        };
        vm.onStart = function () {
            toaster.info("Drag started on vertical slider");
        };
        vm.onStop = function () {
            toaster.info("Drag ended on vertical slider");
        };
        vm.onCancel = function() {
            toaster.info("Drag canceled on vertical slider");
        };
    }

    function DemoHorizontalSlidableController(toaster) {
        var vm = this;

        vm.left = 0;
        vm.top = 0;
        vm.step = 10;

        vm.containerWidth = 200;

        vm.onDrag = function (event, position) {
            vm.left = position.left;
            vm.top = position.top;
        };
        vm.onStart = function () {
            toaster.info("Drag started on horizontal slider");
        };
        vm.onStop = function () {
            toaster.info("Drag ended on horizontal slider");
        };
        vm.onCancel = function() {
            toaster.info("Drag canceled on horizontal slider");
        };
    }

    function DemoBigSlidableController(toaster) {
        var vm = this;

        vm.left = 0;
        vm.top = 0;
        vm.containerHeight = 200;
        vm.containerWidth = 200;
        vm.verticalStep = 10;
        vm.horizontalStep = 10;

        vm.onDrag = function (event, position) {
            vm.left = position.left;
            vm.top = position.top;
        };
        vm.onStart = function () {
            toaster.info("Drag started on big-area slider");
        };
        vm.onStop = function () {
            toaster.info("Drag ended on big-area slider");
        };
        vm.onCancel = function() {
            toaster.info("Drag canceled on big-area slider");
        };
    }
})();
