import IDraggingPosition = xui.IDraggingPosition;
export default class SlidableController implements xui.ISlidableController  {

    //constructor() {}
    public onDragStart:(args:{event: JQueryMouseEventObject}) => void;
    public onDragStop:(args:{event: JQueryMouseEventObject}) => void;
    public onDrag:(args:{event: JQueryMouseEventObject, position: IDraggingPosition}) => IDraggingPosition;
    public onDragCancel: (args:{event: JQueryKeyEventObject}) => void;
    public onResized: (args: {event: JQueryEventObject}) => void;
    public horizontalStep: number;
    public horizontalStepShift: number;
    public verticalStep: number = 0;
    public verticalStepShift: number = 0;

    public isDragDisabled: boolean;
}
