(function () {
    "use strict";

    Xui.module
        .controller("DemoRangeSliderController", DemoRangeSliderController)
        .controller("DemoSingleSliderController", DemoSingleSliderController)
        .controller("DemoSingleSliderWithTextCtrl", DemoSingleSliderWithTextCtrl);

    function DemoRangeSliderController() {
        var vm = this;

        vm.name = "rangeSlider";
        vm.caption = "Potato";
        vm.unit = "kg";
        vm.enabled = true;
        vm.startValue = 990;
        vm.endValue = 1050;
        vm.min = 910;
        vm.max = 1090;
        vm.step = 10;
        vm.updateOnDrag = false;
        vm.minRange = 20;
        vm.fractionSize = 0;
    }

    function DemoSingleSliderController() {
        var vm = this;

        vm.name = "singleSlider";
        vm.caption = "Tomato";
        vm.unit = "kg";
        vm.enabled = true;
        vm.value = 45;
        vm.min = 10;
        vm.max = 90;
        vm.type = "single";
        vm.step = 5;
        vm.updateOnDrag = false;
        vm.fractionSize = 2;
    }

    function DemoSingleSliderWithTextCtrl() {
        this.name= "singleSliderWithText";
        this.caption = "Tomato";
        this.unit = "kg";
        this.enabled = true;
        this.value = 45;
        this.min = 10;
        this.max = 90;
        this.type = "single";
        this.step = 5;
        this.isTextinputEnabled = true;
        this.updateOnDrag = true;
        this.fractionSize = 0;
    }

})();
