/// <reference path="../../ref.d.ts" />

import IScope = angular.IScope;

export default class SpreadsheetController {

    public static $inject = ["$scope"];

    public itemsSource: any[];
    public headerItems: any[] = _.first(this.itemsSource);

    constructor(private $scope: IScope) {
        if (!this.itemsSource) {
            throw new Error("Parameter items-source must be set!");
        }
    };

    // Make deep copy for later
    // public getItemsCopy = () => {
    //     return _.cloneDeep(this.itemsSource);
    // };

    public generateIndex = (index: number, parentIndex: number) => {
        return this.headerItems[index] + parentIndex;
    };

}
