/// <reference path="../../ref.d.ts" />

import SpreadsheetController from "./spreadsheet-controller";

/**
 * @ngdoc directive
 * @name xui.directive:xuiSpreadsheet
 * @restrict E
 *
 * @description
 * Apollo implementation of spreadsheet.
 *
 * @parameters
 * @param {array} items-source Array of items to be displayed in the table.
 *
 * @example
 *   <example module="xui">
 *       <file src="src/components/spreadsheet/docs/spreadsheet-examples.html" name="index.html"></file>
 *       <file src="src/components/spreadsheet/docs/spreadsheet-examples.js" name="script.js"></file>
 *   </example>
 **/

export class Spreadsheet implements ng.IDirective {
    public restrict = "E";
    public template = require<string>("./spreadsheet-directive.html");
    public replace = true;
    public transclude = true;
    public scope = {};
    public bindToController = {
        itemsSource: "="
    };
    public controller = SpreadsheetController;
    public controllerAs = "vm";
}
