(function() {
    "use strict";

    Xui.controller("DemoSwitchController", DemoSwitchController, ["xuiToastService"]);

    function DemoSwitchController(toastService) {
        var vm = this;

        vm.isSwitchOn = false;
        vm.disabled = true;

        vm.onChange = function() {
            toastService.info("changed!");
        };
    }
})();
