import {ITabController} from "./tab-controller";
import {ITabsRouterController} from "./tabsRouter-controller";

export interface ITabRouterController {
    tabController: ITabController;
    configuration: ITabRouterConfiguration;
    init: (tabsRouterController: ITabsRouterController, tabController: ITabController) => void;
}

export interface ITabRouterConfiguration {
    params?: {};
    options?: angular.ui.IStateOptions;
}

export class TabRouterController implements ITabRouterController {
    public tabController: ITabController;
    public configuration: ITabRouterConfiguration;
    public init = (tabsRouterController: ITabsRouterController, tabController: ITabController): void => {
        this.tabController = tabController;
        tabsRouterController.registerTabRouter(this);
    };
}

export default TabRouterController;
