import Module from "../../common/module";
import Textbox from "./textbox-directive";
import TextboxController from "./textbox-controller";
import {ModelWarning} from "./model-warning-directive";
import xuiStatic from "../../xui-static";

export default (module:Module)=> {
    module.component("xuiTextbox", Textbox);
    module.controller("xuiTextboxController", TextboxController);
    module.component("xuiModelWarning", ModelWarning);

    const translations = require("./textbox-translations.json") as {[key:string]:string};
    module.app("components").config(xuiStatic.registerTranslations(translations));
};
