import Module from "../../common/module";
import TimeframePickerInline from "./timeframePickerInline-directive";
import TimeframePickerInlineController from "./timeframePickerInline-controller";
import TimeframePicker from "./timeframePicker-directive";
import TimeframePickerController from "./timeframePicker-controller";
import TimeframeService from "./timeframe-service";
import xuiStatic from "../../xui-static";

export default (module:Module)=> {
    module.component("xuiTimeframePickerInline", TimeframePickerInline);
    module.controller("xuiTimeframePickerInlineController", TimeframePickerInlineController);
    module.component("xuiTimeframePicker", TimeframePicker);
    module.controller("xuiTimeframePickerController", TimeframePickerController);
    module.service("xuiTimeframeService", TimeframeService);

    const translations = require("./timeframePicker-translations.json") as {[key:string]:string};
    module.app("components").config(xuiStatic.registerTranslations(translations));
};
