import "./toast-service.less";
import Module from "../../common/module";
import ToastService from "./toast-service";
import {ToastServiceObsolete} from "./toast-service";
import Toast from "./toast-directive";
import ToastController from "./toast-controller";

export default (module:Module)=> {
    module.service("xuiToastService", ToastService);
    module.service("toastService", ToastServiceObsolete);
    module.component("xuiToast", Toast);
    module.controller("xuiToastController", ToastController);
};
