(function () {
    "use strict";

    Xui.module
        .controller("DemoToolbarController", DemoToolbarController, ["xuiToastService"]);

    function DemoToolbarController(toastService) {
        var vm = this;

        vm.toolbarButtonClicked = function (msg) {
            toastService.success("Clicked toolbar item: " + msg);
        };
        vm.onMenuItemAction = function () {
            toastService.success("Menu item clicked");
        };
    }
})();
