/**
 * @ngdoc directive
 * @name xui.directive:xuiToolbar
 * @restrict E
 *
 * @description
 * XUI wrapper for toolbar control. It groups toolbar items (xui-toolbar-item),
 * which can be aligned to the left or right side of the bar. Items are aligned to the left by default.
 * Use xui-toolbar-splitter to determine the place, where the right alignment starts.
 * Use xui-toolbar-divider to display a vertical line between items.
 *
 * @example
 *    <example module="xui">
 *        <file src="src/components/toolbar/docs/toolbar-examples.html" name="index.html"></file>
 *        <file src="src/components/toolbar/docs/toolbar-examples.js" name="app.js"></file>
 *    </example>
 */

export default class Toolbar implements ng.IDirective {
    public static $inject = ["swUtil"];
    constructor(private swUtil:xui.IUtility) {}

    public restrict = "E";
    public templateUrl = "xui/components/toolbar/toolbar-directive.html";
    public replace = true;
    public transclude = true;
    public scope = {};

    public link = (scope:ng.IScope, element:ng.IAugmentedJQuery,
                   attrs:ng.IAttributes) => {
        this.swUtil.watchBoolean(attrs, "isActive", null, (newValue:boolean) => {
            element.toggleClass("xui-toolbar--active", newValue);
        });
    };
}
