import SpecHelper from "../../specHelper";

describe("components >", () => {
    describe("toolbar >", () => {
        describe("splitter >", () => {
            let element:any, scope:any, compile:any;

            beforeEach(angular.mock.module("xui"));

            beforeEach(inject(function ($compile:any, $rootScope:any) {
                compile = $compile;
                scope = $rootScope.$new();
            }));

            let directive = "<xui-toolbar-splitter><div class='content'></div></xui-toolbar-splitter>";

            describe("replace >", () => {
                it("should have directive element replaced", () => {
                    // act
                    element = SpecHelper.getCompiledElement(compile, scope, directive);

                    // assert
                    expect(element[0].tagName.toLowerCase()).not.toEqual("xui-toolbar-splitter");
                });
            });
        });
    });
});
