import Module from "../../common/module";

import TreeviewChildren from "./treeview-children-directive";
import {Treeview} from "./treeview-directive";
import TreeviewKeyboard from "./treeview-keyboard-directive";
import TreeviewTransclude from "./treeview-transclude-directive";
import {TreeviewService} from "./treeview-service";
import TreeviewLimit from "./treeview-filter";
import xuiStatic from "../../xui-static";

export default (module:Module)=> {
    module.component("xuiTreeviewChildren", TreeviewChildren);
    module.component("xuiTreeview", Treeview);
    module.filter("treeviewLimit", TreeviewLimit);
    module.component("xuiTreeviewKeyboard", TreeviewKeyboard);
    module.service("xuiTreeviewService", TreeviewService);
    module.component("xuiTreeviewTransclude", TreeviewTransclude);

    const translations = require("./treeview-translations.json") as {[key:string]:string};
    module.app("components").config(xuiStatic.registerTranslations(translations));
};
