import IVirtualNode = xui.IVirtualNode;
import ITreeviewOptions = xui.ITreeviewOptions;

/* @ngInject */
export default function TreeviewLimit($filter:ng.IFilterService) {
    return function (input:IVirtualNode[], parent:any, options:ITreeviewOptions) {
        if (parent.limitMultiplier === null) {
            return input;
        }
        const threshold = parent.limitMultiplier * options.limit;
        //console.log("Limiting to: ", threshold);
        return $filter("limitTo")(input, threshold);
    };
}
