import ITreeviewScope = xui.ITreeviewScope;

export class TreeviewTransclude {
    public restrict = "A";
    public replace = false;
    public require = "^xuiTreeview";

    public link(scope:ITreeviewScope, iElement:ng.IAugmentedJQuery, iAttrs:ng.IAttributes, treeviewCtrl:any) {
        treeviewCtrl.transcludeFn(scope, (clone:any) => {
            if (scope.node) {
                iElement.append(clone);
            }
        });
    }
}

export default TreeviewTransclude;
