import Module from "../../common/module";
import WizardController from "./wizard-controller";
import WizardDialogController from "./wizard-dialog-controller";
import Wizard from "./wizard-directive";
import WizardSummaryTransclude from "./wizard-summary-transclude";
import WizardStepController from "./wizard-step-controller";
import WizardStep from "./wizard-step-directive";
import WizardSummary from "./wizard-summary-directive";
import WizardDialogService from "./wizard-dialog-service";
import xuiStatic from "../../xui-static";

export default (module:Module)=> {
    module.component("xuiWizard", Wizard);
    module.component("xuiWizardStep", WizardStep);
    module.component("xuiWizardSummary", WizardSummary);
    module.component("xuiWizardSummaryTransclude", WizardSummaryTransclude);
    module.controller("xuiWizardController", WizardController);
    module.controller("xuiWizardStepController", WizardStepController);
    module.controller("xuiWizardDialogController", WizardDialogController);
    module.service("xuiWizardDialogService", WizardDialogService);

    const translations = require("./wizard-translations.json") as {[key:string]:string};
    module.app("components").config(xuiStatic.registerTranslations(translations));
};
