/// <reference path="../../ref.d.ts" />

import SpecHelper from "../../specHelper";

describe("layouts >", () => {
    describe("strip >", () => {
        describe("splitter >", () => {
            let element:any, scope:any, compile:any;

            beforeEach(angular.mock.module("xui"));

            beforeEach(inject(function ($compile:any, $rootScope:any) {
                compile = $compile;
                scope = $rootScope.$new();
            }));

            let directive = "<xui-strip-layout-splitter><div class='content'></div></xui-strip-layout-splitter>";

            describe("replace >", () => {
                it("should have replaced directive element", () => {
                    // act
                    element = SpecHelper.getCompiledElement(compile, scope, directive);

                    // assert
                    expect(element[0].tagName.toLowerCase()).not.toEqual("xui-strip-layout-splitter");
                });
            });
        });
    });
});
